import React, { useState, useEffect } from 'react';
import TodoForm from './TodoForm';
import FilterButtons from './FilterButtons';

function TodoList() {
  const [todos, setTodos] = useState(() => {
    const savedTodos = localStorage.getItem('todos');
    return savedTodos ? JSON.parse(savedTodos) : [];
  });
  const [filter, setFilter] = useState('all');
  const [editingId, setEditingId] = useState(null);
  const [editingText, setEditingText] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);

  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const addTodo = (text) => {
    if (!text.trim()) return;
    setTodos([...todos, { 
      id: Date.now(), 
      text, 
      completed: false,
      date: selectedDate 
    }]);
  };

  const toggleTodo = (id) => {
    setTodos(todos.map(todo => 
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    ));
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  const startEditing = (id, text) => {
    setEditingId(id);
    setEditingText(text);
  };

  const saveEdit = (id) => {
    if (!editingText.trim()) return;
    setTodos(todos.map(todo =>
      todo.id === id ? { ...todo, text: editingText } : todo
    ));
    setEditingId(null);
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === 'active') return !todo.completed;
    if (filter === 'completed') return todo.completed;
    return true;
  });

  // Grouper les todos par date
  const todosByDate = todos.reduce((acc, todo) => {
    if (!acc[todo.date]) {
      acc[todo.date] = [];
    }
    acc[todo.date].push(todo);
    return acc;
  }, {});

  return (
    <div className="app-container">
      <div className="calendar-container">
        <h2>Calendrier</h2>
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
          className="date-picker"
        />
        <div className="date-todos">
          {Object.entries(todosByDate).map(([date, dateTodos]) => (
            <div key={date} className="date-group">
              <h3>{new Date(date).toLocaleDateString('fr-FR')}</h3>
              <ul>
                {dateTodos.map(todo => (
                  <li key={todo.id} className={todo.completed ? 'completed' : ''}>
                    {todo.text}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>

      <div className="todo-container">
        <h1>Ma Todo List</h1>
        <TodoForm onSubmit={addTodo} selectedDate={selectedDate} />
        <FilterButtons filter={filter} setFilter={setFilter} />
        <ul className="todo-list">
          {filteredTodos.map((todo) => (
            <li key={todo.id} className={todo.completed ? 'completed' : ''}>
              {editingId === todo.id ? (
                <div className="edit-form">
                  <input
                    type="text"
                    value={editingText}
                    onChange={(e) => setEditingText(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && saveEdit(todo.id)}
                  />
                  <button onClick={() => saveEdit(todo.id)}>Sauvegarder</button>
                  <button onClick={() => setEditingId(null)}>Annuler</button>
                </div>
              ) : (
                <>
                  <input
                    type="checkbox"
                    checked={todo.completed}
                    onChange={() => toggleTodo(todo.id)}
                  />
                  <span>{todo.text}</span>
                  <div className="todo-actions">
                    <button className="edit-btn" onClick={() => startEditing(todo.id, todo.text)}>
                      Modifier
                    </button>
                    <button className="delete-btn" onClick={() => deleteTodo(todo.id)}>
                      Supprimer
                    </button>
                  </div>
                </>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default TodoList;
